mapper.select:
select id, zonename, mobname, killer, dietime, mapname, relivetime
from monster_die
where id = #{id,jdbcType=INTEGER}

select id, zonename, mobname, killer, dietime, mapname, relivetime
from monster_die
where zonename = #{zonename,jdbcType=VARCHAR}

select id, zonename, mobname, killer, dietime, mapname, relivetime
from monster_die
where zonename = #{zonename,jdbcType=VARCHAR} AND mobname = #{mobname,jdbcType=VARCHAR} AND mapname = #{mapname,jdbcType=VARCHAR}
