mapper.update:
update monster_die
set zonename = #{zonename,jdbcType=VARCHAR},
  mobname = #{mobname,jdbcType=VARCHAR},
  killer = #{killer,jdbcType=VARCHAR},
  dietime = #{dietime,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, zonename, mobname, killer, dietime
from monster_die
where id = #{id,jdbcType=INTEGER}
