mapper.update:
update web_action
set zoneid = #{zoneid,jdbcType=VARCHAR},
  actiontype = #{actiontype,jdbcType=INTEGER},
  actiondata = #{actiondata,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, zoneid, actiontype, actiondata
from web_action
where id = #{id,jdbcType=INTEGER}

select id, zoneid, actiontype, actiondata
from web_action
where zoneid = #{zoneid,jdbcType=VARCHAR}
